#!/bin/bash

FLASH_NAME="FABlin-1.0.0097.1"
EE_FILE="EE.hex"

EE64="" 
#MARLIN64=""

FLASH_FILE="${FLASH_NAME}_wbl.hex"
port="usb" 
programmer="avrispmkii"
opts="-U eeprom:w:$EE_FILE:i"
fuses="-U lfuse:w:0xFF:m -U hfuse:w:0xDA:m -U efuse:w:0xF5:m -U lock:w:0x3F:m"
        
for arg in "$@"; do
    if [ "$arg" = "noee" ]; then
        opts=""
    fi

    if [ "$arg" = "arduino" ]; then
        port="/dev/ttyAMA0 -b 57600"
        programmer="arduino"
        fuses=""
    fi

    if [ "$arg" = "nobl" ]; then
        FLASH_FILE="${FLASH_NAME}_nobl.hex"
    fi

done

if [[ -n ${MARLIN64+x} ]]; then
    echo $MARLIN64 | base64 -d - > $FLASH_FILE
fi

if [[ -n ${EE64+x} && -n $opts ]]; then
    echo $EE64 | base64 -d - > $EE_FILE
fi

if command -v avrdude-original >/dev/null 2>&1; then
    PROGRAM=avrdude-original
else
    PROGRAM=avrdude
fi

if [ "$programmer" = "arduino" ]; then
    python /var/www/fabui/python/force_reset.py
fi
$PROGRAM -u -q -p atmega1280 -P $port -c $programmer $fuses -U flash:w:$FLASH_FILE:i $opts


if [ $? -ne 0 ]; then
    echo Something went wrong. :\(
else
    if [[ -n ${MARLIN64+x} ]]; then
        rm $FLASH_FILE
    fi

    if [[ -n ${EE64+x} && -n $opts ]]; then
        rm $EE_FILE
    fi

    killall php python
    python /var/www/fabui/python/force_reset.py
    sleep 5
    /etc/init.d/fabui start
fi
